import React, { Component } from 'react';
import axios from 'axios';

// import Header from './modules/Header';
// import Input from './modules/Input';
import Weather from './modules/Weather';

// import './style.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      weather: [],
      temp: [], //gets info from response.data.list[0].main.temp
      clouds: [], //gets info from response.data.list[0].weather[0].description
      city:''
    };
  }

  getWeather = (cityname) => {

    axios.get(`https://api.openweathermap.org/data/2.5/find?q=${cityname}&units=imperial&appid=f92c1f4990b0574d4a4e4d3dd556f388`)
      .then(response => {
        this.setState({
          weather: response.data.list[0],
          temp: response.data.list[0].main.temp, //pushes data to this.state.temp
          clouds: response.data.list[0].weather[0].description //pushes data to this.state.clouds
        });
      })
      .catch(error => {
        console.log('Error', error);
      });
  };

  updateInfo = (e) => {
    this.setState({
      city: e.target.value
    })

    // this.getWeather(city)
    // if (event.key === 'Enter') {
    //   event.preventDefault();

    //   this.getWeather();
    // }
  }

  render() {
    if (this.state.weather === undefined) {
      return (<div>Fetching weather, please wait..</div>)
    } else {
      return (<Weather 
        updateInfo={this.updateInfo}
        weatherData={this.state.weather}

        temp={this.state.temp}
        cityvalue={this.state.cityvalue}
        />)
    }
  }
}

export default App;
