import React, {useState, useEffect} from 'react';
import './App.css';
import {getWeatherData} from './data/weatherapi';
// import rainy from './images/Rain.png';
// import sunny from './images/Sunny.png';
// import snowy from './images/Snowy.png';
// import cloudy from './images/02d.png';

function App() {
  const [weatherdata, setWeatherdata] = useState(null);
  const [imgdata, setImgdata] = useState(null);
  const [img, setImg] = useState([ottawa='Ottawa', london='London'])
  const [city, setCity] = useState('Ottawa');
  const [temp, setTemp] = useState(null);
  const [loading, setLoading] = useState(false);
  const [toggle, setToggle] = useState(true);



  const getData = async () => {
    try{
      const data = await getWeatherData(city);
      setWeatherdata(data);
      console.log(data); 
    }catch(error){
      console.log(error.message);
    }
  }

  const handleClick = () => {
    if(toggle === false){
      setTemp(Math.round(weatherdata.main.temp - 273.15)+ '°C');
      setToggle(true);
      console.log('f to c');
    }
    if(toggle === true){
      setTemp(Math.round(weatherdata.main.temp + '°F'));
      setToggle(false);
      console.log('c to f');
    }
  }
  const updateInfo = (cityName) =>{
    setCity(cityName);
  }

  const getImg = async (a) => {
    try{
      setImgdata(await getWeatherData(img[a]));
      console.log(data); 
    }catch(error){
      console.log(error.message);
    }
  }

  useEffect(() => {
    getData();
    getImg();
  }, [city]);
  return (
    <div className="App">
      <header className="header">
        <div className="title">
        <h2><b>Weather</b></h2>
        </div>
        <label className="switch" >
          <input type="checkbox" id="togBtn" />
            <div className="slider round" onClick={handleClick}>
              <span className="f_degree">&deg;F</span>
              <span className="c_degree">&deg;C</span>
            </div>
        </label>
      </header>

      {weatherdata !== null ? (
        <div className="content">
        <div className="row">
          <table className="col-4 weather-col">
            <tbody>
            <tr><td className="tab-title" >
            Favorite Locations
            </td></tr>
            <tr><td className="city-lists" onMouseOver={()=>{updateInfo('Ottawa')}}>
             <img src={
              `${process.env.PUBLIC_URL}/assets/images/${imgdata(0).weather[0].main}.png`
              } alt="weatherimg" className="weatherimg" /> 
              <span className="city">Ottawa, Canada</span>
            </td></tr>
            <tr><td className="city-lists" onMouseOver={()=>{updateInfo('Beijing')}}>
            <img src={`https://openweathermap.org/img/w/02d.png`} alt="weatherimg" className="weatherimg" />
              <span className="city">Beijing, Canada</span>
            </td></tr>
            <tr><td className="city-lists" onMouseOver={()=>{updateInfo('Alaska')}}>
              {/* <img src={sunny} alt="weatherimg" className="weatherimg" /> */}
              <span className="city">Alaska, Canada</span>
            </td></tr>
            <tr><td className="city-lists" onMouseOver={()=>{updateInfo('Irvine')}}>
              {/* <img src={rainy} alt="weatherimg" className="weatherimg" /> */}
              <span className="city">Irvine, Canada</span>
            </td></tr>
            <tr className="emp"></tr>
            </tbody>
          </table>

          <table className="col-7 offset-1 weather-col">
          <tbody>
          <tr><td className="tab-title">{weatherdata.name}, ON</td></tr>
          <tr className="detail"><td>
           <img src={`${process.env.PUBLIC_URL}/assets/images/${weatherdata.weather[0].main}.png`} alt="weatherimg" className="weatherimg" />
          <div className="detail-info">
            <div className="temp">{toggle === true 
            ? Math.round(weatherdata.main.temp - 273.15)+ '°C' 
            : weatherdata.main.temp + '°F'}</div>
            <span>{weatherdata.weather[0].description}</span>
          </div>
          </td>
          </tr>
          </tbody>
          </table>
        </div>
      </div>
      ) : null}
    </div>
  );
}

export default App;
